import React, { useState } from 'react';

function App() {
  const [result, setResult] = useState(null);

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append("file", file);

    const response = await fetch("http://localhost:8000/upload", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    console.log("API result:", data);
    setResult(data.result.summary); // assuming backend returns this structure
  };

  const styles = {
    container: {
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      padding: '40px',
      maxWidth: '800px',
      margin: 'auto',
      backgroundColor: '#f9f9f9',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      textAlign: 'center',
      color: '#2c3e50',
    },
    fileInput: {
      display: 'block',
      margin: '20px auto',
      padding: '10px',
      fontSize: '16px',
      cursor: 'pointer',
    },
    resultContainer: {
      marginTop: '30px',
      backgroundColor: '#ffffff',
      padding: '20px',
      borderRadius: '8px',
      border: '1px solid #ddd',
    },
    title: {
      color: '#34495e',
    },
    language: {
      fontStyle: 'italic',
      color: '#7f8c8d',
    },
    pre: {
      backgroundColor: '#ecf0f1',
      padding: '12px',
      borderRadius: '6px',
      overflowX: 'auto',
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>üìÑ LLM Extractor</h1>
      <input
        type="file"
        onChange={handleUpload}
        style={styles.fileInput}
      />

      {result && (
        <div style={styles.resultContainer}>
          <h2 style={styles.title}>üìå Title: {result.title}</h2>
          <p style={styles.language}>üó£Ô∏è Language: {result.language}</p>

          <h3>üß© Outline:</h3>
          <pre style={styles.pre}>{JSON.stringify(result.outline, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
