import React, { useState } from 'react';

function App() {
  const [result, setResult] = useState(null);

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append("file", file);

    const response = await fetch("http://localhost:8000/upload", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    console.log("API result:", data);
    setResult(data.result.summary);
  };

  return (
    <div style={{
      fontFamily: 'Segoe UI, sans-serif',
      background: 'linear-gradient(to right, #fdfbfb, #ebedee)',
      minHeight: '100vh',
      padding: '40px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    }}>
      <h1 style={{
        fontSize: '2.5rem',
        fontWeight: '700',
        color: '#4a4e69',
        marginBottom: '20px'
      }}>
        üìÑ LLM Extractor
      </h1>

      <label
        htmlFor="file-upload"
        style={{
          backgroundColor: '#4a4e69',
          color: 'white',
          padding: '12px 24px',
          borderRadius: '8px',
          cursor: 'pointer',
          transition: '0.3s ease',
          fontWeight: '500',
          fontSize: '16px'
        }}
      >
        Upload a File
        <input
          id="file-upload"
          type="file"
          onChange={handleUpload}
          style={{ display: 'none' }}
        />
      </label>

      {result && (
        <div style={{
          backgroundColor: '#ffffff',
          boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
          borderRadius: '16px',
          padding: '30px',
          marginTop: '30px',
          width: '100%',
          maxWidth: '700px',
          animation: 'fadeIn 0.5s ease-in-out'
        }}>
          <h2 style={{
            color: '#22223b',
            fontSize: '24px',
            fontWeight: 'bold',
            marginBottom: '10px'
          }}>
            üìù Title: <span style={{ fontWeight: '500' }}>{result.title}</span>
          </h2>

          <p style={{ fontSize: '18px', color: '#4a4e69', marginBottom: '20px' }}>
            üåê Language: <strong>{result.language}</strong>
          </p>

          <h3 style={{ color: '#22223b', fontSize: '20px', marginBottom: '10px' }}>
            üìå Outline:
          </h3>
          <pre style={{
            background: '#f8f9fa',
            padding: '20px',
            borderRadius: '8px',
            fontSize: '14px',
            color: '#1a1a1a',
            whiteSpace: 'pre-wrap',
            overflowX: 'auto'
          }}>
            {JSON.stringify(result.outline, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
